void putstr(char *putstr);

int find_newline(t_list *node)
{
	int i;

	i = 0;
	if(node == NULL)
		return (1);
	else
	{
		while(node->buffer[i] != '\0')
		{
			if(node->buffer[i] == '\n' || node->buffer[i] == EOF)
				return (0);
			i++;
		}
	}
	return (1);
}

int size_of_total_str(t_list **list)
{
	t_list *node;
	int i;
	int size;

	size = 1;
	i = 0;
	node = *list;
	while(node != NULL)
	{
		while(node->buffer[i])
		{
			if(node->buffer[i] == '\n')
				return(size + i + 1);
			i++;
		}
		size = size + i;
		i = 0;
		node = node->next;
	}
	return (size);
}

char *make_next_line(t_list **list, int size)
{
	t_list *node;
	int i;
	int str_i;
	char *bigstring;

	bigstring = calloc(sizeof(char) , size + 1);

	i = 0;
	str_i = 0;
	node = *list;
	while(node != NULL)
	{
		while(node->buffer[i])
		{
			bigstring[str_i] = node->buffer[i];
			if(node->buffer[i] == '\n')
				return(bigstring);
			i++;
			str_i++;
		}
		i = 0;
		node = node->next;
	}
	return (bigstring);
}


char *ret_next_line(t_list **list)
{
	char *next_line;
	int size;

	next_line = NULL;
	size = size_of_total_str(list);
	if(size > 1)
		next_line = make_next_line(list, size);
	return (next_line);
}

