size_t	ft_strlcpy(char *dst, const char *src, size_t dstsize)
{
	size_t	srcsize;
	size_t	i;

	if (!dst || !src)
		return (0);
	srcsize = strlen(src);
	i = 0;
	if (dstsize != 0)
	{
		while (src[i] != '\0' && i < (dstsize - 1))
		{
			dst[i] = src[i];
			//write(1,&dst[i],1);
			i++;
		}
		dst[i] = '\0';
	}
	return (srcsize);
}


char *create_list(int fd, t_list **list)
{
	int bytesread;
	char *buff_str;
	char *buff_str1;
	t_list *new_node;
	t_list *last_node;
	buff_str1 = calloc(sizeof(char), BUFFER_SIZE + 1);
	buff_str = calloc(sizeof(char), BUFFER_SIZE + 1);// callocte eerst aleen buf_size, daardoor geen \0 en dus invalid write bij andere functie
	last_node = *list;
	while(find_newline(last_node))//go to last of list and find
	{
		bytesread = read(fd, buff_str1, BUFFER_SIZE);
		if(!bytesread)
			break;
		//write(1,"|",1);
		//ft_putnbr_fd(bytesread,1);
		//write(1,"|",1);
		ft_strlcpy(buff_str, buff_str1, bytesread + 1);
		//putstr(buff_str);
		new_node = ft_lstnew_strdup(buff_str);//malloc lstbuffer & lst
		if(!new_node)
			break ;//free&return
		ft_lstadd_back(list, new_node);//add to back if there is list, else make *lst = new
		last_node = ft_lstlast(*list);
	}
	free(buff_str1);
	return(buff_str);
}

