void putstr(char *putstr)
{
	int size;
	size = 0;
	while(putstr[size])
		size++;
	//char null[] = "\n";
	write(1, putstr, size);
}

char	*ft_strcmp_str(char *s1, char *s2)
{
	int  i;

	i = 0;
	
	while(s1[i] != '\0' && s2[i] != '\0')
	{
		//write(1, &s1[i],1);
		if(s1[i] != s2[i])
			return (&s1[i]);
		i++;
	}
	if(s1[i] != s2[i])
			return (&s2[i]);
	return (NULL);
}

